import numpy as npimport h5pyimport os    class Follower():    def __init__(self,                 key_datasets = {}):        self._keyindex = 0        self.start_reading = False        self.key_datasets = key_datasets                                            def can_start_reading(self):            self.start_reading = True        can_read = True        for dict_key, ds in self.key_datasets.items():            if (ds == 0).all():                can_read = can_read and False                    self.start_reading = can_read                            def current_max(self):                max_array = []                for dict_key, ds in self.key_datasets.items():            temp_array = (np.asarray(ds)).flatten()            max_array += [np.max(np.nonzero(temp_array))]                max_array = np.asarray(max_array)        return np.min(max_array)            def update_keys(self, test_arr_dict):        self.key_datasets = test_arr_dict                    #                        